mapper.select:
SELECT categoryno, categorygrp, categoryname, sortno, visible, rdate
FROM category
WHERE categoryno = #{categoryNo}

mapper.update:
UPDATE category
SET categorygrp = #{categoryGrp}, categoryname = #{categoryName}, sortno = #{sortNo}, visible = #{visible}, rdate = SYSDATE
WHERE categoryno = #{categoryNo}

UPDATE category
SET sortno = sortno - 1
WHERE categoryno = #{categoryno}

UPDATE category
SET sortno = sortno + 1
WHERE categoryno = #{categoryNo}

UPDATE category
SET visible = 'Y'
WHERE categoryno = #{categoryNo}

UPDATE category
SET visible = 'N'
WHERE categoryno = #{categoryNo}

mapper.select:
SELECT categoryno, categorygrp, categoryname, sortno, visible, rdate, r FROM ( SELECT categoryno, categorygrp, categoryname, sortno, visible, rdate, rownum as r FROM ( SELECT categoryno, categorygrp, categoryname, sortno, visible, rdate FROM category
WHERE (UPPER(categorygrp) LIKE '%' || UPPER(#{word}) || '%') OR (UPPER(categoryname) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY sortno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
