mapper.select:
SELECT userno, username, useremail, userpassword, usergrade, rdate
FROM bloguser
WHERE userno = #{userno}

mapper.update:
UPDATE bloguser
SET username=#{username}, useremail=#{useremail}, userpassword=#{userpassword}, rdate=SYSDATE
WHERE userno=#{userno}

UPDATE bloguser
SET usergrade = usergrade + 1
WHERE userno = #{userno}

UPDATE bloguser
SET usergrade = usergrade - 1
WHERE userno = #{userno}
