mapper.select:
SELECT cateno, grp, name, cnt, seqno, visible, rdate
FROM cate
WHERE cateno = #{cateno}

mapper.update:
UPDATE cate
SET grp=#{grp}, name=#{name}, seqno=#{seqno}, visible=#{visible}, rdate=SYSDATE
WHERE cateno = #{cateno}

UPDATE cate
SET seqno = seqno - 1
WHERE cateno = #{cateno}

UPDATE cate
SET seqno = seqno + 1
WHERE cateno = #{cateno}

UPDATE cate
SET visible = 'Y'
WHERE cateno = #{cateno}

UPDATE cate
SET visible = 'N'
WHERE cateno = #{cateno}
